import { Switch, Route, Redirect } from "react-router-dom";
import { useContext } from "react";
import Layout from "./components/Layout/Layout";
import UserProfile from "./components/Profile/UserProfile";
import AuthPage from "./pages/AuthPage";
import HomePage from "./pages/HomePage";
import AppContext from "./components/store/app-context";

function App() {
  const appCxt = useContext(AppContext);

  const token = localStorage.getItem("token");

  if (token) {
    const validTill = localStorage.getItem(validTill);
    const timeLeft = new Date() - validTill;
    if (timeLeft > 3000) {
      appCxt.login(token, timeLeft);
      const sessionTimer = setTimeout(appCxt.logout(), timeLeft);
    }
  }

  return (
    <Layout>
      <Switch>
        <Route path="/" exact>
          <HomePage />
        </Route>
        <Route path="/auth">
          <AuthPage />
        </Route>
        {appCxt.isLoggedIn && (
          <Route path="/profile">
            <UserProfile />
          </Route>
        )}
        <Route path="*">
          <Redirect to="/" />
        </Route>
      </Switch>
    </Layout>
  );
}

export default App;
